function shuffleArray<T>(array: T[]): T[] {
  const arr = array.slice();
  for (let i = arr.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [arr[i], arr[j]] = [arr[j], arr[i]];
  }
  return arr;
}

function randomizeQuestion(q: Question): Question {
  if (!q.reponses || typeof q.bonneReponse !== 'number') return q;
  const correct = q.reponses[q.bonneReponse];
  const shuffled = shuffleArray(q.reponses);
  const newIndex = shuffled.indexOf(correct);
  return { ...q, reponses: shuffled, bonneReponse: newIndex };
}

function randomizeQuestionsArray(arr: Question[]): Question[] {
  return arr.map(randomizeQuestion);
}

