// Fonction pour obtenir des questions aléatoires d'un thème
export const obtenirQuestionsParTheme = (theme: string, nombre: number = 20): Question[] => {
  const questions = questionsParTheme[theme] || [];
  const questionsMelangees = [...questions].sort(() => Math.random() - 0.5);
  return questionsMelangees.slice(0, nombre);
};

// Fonction pour obtenir des questions aléatoires de tous les thèmes
export const obtenirQuestionsAleatoires = (nombreQuestions: number = 20): Question[] => {
  const toutesLesQuestions: Question[] = [];
  
  Object.values(questionsParTheme).forEach(questions => {
    toutesLesQuestions.push(...questions);
  });
  
  const questionsMelangees = [...toutesLesQuestions].sort(() => Math.random() - 0.5);
  return questionsMelangees.slice(0, nombreQuestions);
};

// Fonction pour le challenge des 100 questions
export const obtenirChallenge100Questions = (): Question[] => {
  return obtenirQuestionsAleatoires(100);
};

// Fonction pour obtenir des questions Génie en Herbe
export const obtenirQuestionsGenieEnHerbe = (rubrique: string, nombre: number = 10): QuestionGenieEnHerbe[] => {
  const questions = questionsGenieEnHerbe[rubrique] || [];
  const questionsMelangees = [...questions].sort(() => Math.random() - 0.5);
  return questionsMelangees.slice(0, nombre);
};

